void drawPac(int x, int y, int clear, int c)
{
    short int c1;
    if (clear == 1)
    {
        c1 = BLACK;
    }
    else
    {
        c1 = 0xff20;
    }
    bool face = ((c % 10) >= 5);

    if (face || clear == 1)
    {
        drawPacClosed(x, y, c1);
    }
    else
    {
        drawPacOpen(x, y, c1);
    }
}